mapper.select:
 select 

 from topic_savemoney_log where 1=1

 	and id = #{id}


 	and m_id = #{mId}


 	and tg_id = #{tgId}


 	and goods_sku_id = #{goodsSkuId}


		and act_no like CONCAT(CONCAT('%', #{actNo}), '%')


		and my_no like CONCAT(CONCAT('%', #{myNo}), '%')


		and order_no like CONCAT(CONCAT('%', #{orderNo}), '%')

 order by add_time desc

select act_no
from topic_savemoney_log
where my_no = #{myNo,jdbcType=VARCHAR} 

and m_id = #{mId,jdbcType=INTEGER}

select 

from topic_savemoney_log
where m_id = #{0} and tg_id = #{1}
