mapper.select:
select 

from order_main
where id = #{id,jdbcType=INTEGER}

  	select *
   	from order_main

    	id in (select order_id from order_shop where shop_id = #{0} and is_refund = #{5}

        	and status = #{status,jdbcType=INTEGER}

    	)
    	and order_no like CONCAT(CONCAT('%', #{2}), '%')
	     and addtime >=#{3}
        and addtime <=#{4}

    order by addtime desc

  	select *
   	from order_main where
    order_no like CONCAT(CONCAT('%', #{1}), '%')

	    and status= #{status,jdbcType=INTEGER}

	 and addtime >=#{2}
	 and addtime <#{3}
    order by addtime desc

select * from order_main
id in (select order_id from order_shop where shop_id = #{0}) and order_no like CONCAT(CONCAT('%', #{1}), '%') and addtime >=#{2} and addtime <=#{3}
order by addtime desc

