mapper.select:
select  count(*) from order_main where id in(
	select distinct a.id 
	from order_main a left join order_sku b 
	on a.id = b.order_id where b.is_refund=#{0} and b.d_state=#{1} 
	and a.order_no like CONCAT(CONCAT('%', #{2}), '%')
)

select  count(*) from order_main where id in(
	select distinct a.id 
	from order_main a left join order_sku b 
	on a.id = b.order_id where b.is_refund=#{0} and b.d_state=#{1} and a.is_shop_checkout=#{3}
	and a.order_no like CONCAT(CONCAT('%', #{2}), '%')
)

select count(*)
from order_main


   		status = #{status,jdbcType=INTEGER}

select count(*)
from order_shop a LEFT JOIN order_main b on a.order_id = b.id
where a.status = #{3} 
 and b.addtime >=#{1}
 and b.addtime <=#{2}


and a.is_refund = 0

	and a.shop_id = #{shopId}

select count(*)
from order_shop
where status = #{1} 


and is_refund = 0

	and shop_id = #{shopId}

select count(*)
from order_shop a LEFT JOIN order_main b on a.order_id = b.id
where a.status = 2 
 and b.addtime >=#{0}
 and b.addtime <=#{1}
and a.is_refund = 0
and a.order_no not in
(select order_no from order_team where status!=1)

	and a.shop_id = #{shopId}

select count(id)
from order_shop
where status = #{1} 
and is_refund = 0 and jdOrderId !='0'

	and shop_id = #{shopId}

select count(*)
from order_shop a LEFT JOIN order_main b on a.order_id = b.id
where  a.status != 1 and a.status != 6 and a.status != 8
 and b.addtime >=#{0}
 and b.addtime <=#{1}

	and a.shop_id = #{shopId}

select count(*)
from order_main


   		status = #{status,jdbcType=INTEGER}

select count(*)
from order_main
where m_id = #{mId,jdbcType=INTEGER}

select sum(order_price)
from order_main
where m_id = #{mId,jdbcType=INTEGER}

select a.* 
from order_main a LEFT JOIN order_shop b on a.id = b.order_id LEFT JOIN order_sku c ON a.id = c.order_id LEFT JOIN member d ON a.m_id = d.id
where 1=1

	and a.status = #{status}


	and a.order_no like CONCAT(CONCAT('%', #{orderNo}), '%')


	and b.shop_order_no like CONCAT(CONCAT('%', #{shopOrderNo}), '%')


   and  a.addtime >= #{startTime}


   and  a.addtime <= #{endTime}


	and d.id = #{id}


	and a.shop_id = #{shopId}


	and d.phone like CONCAT(CONCAT('%', #{tel}), '%')


	and d.username like CONCAT(CONCAT('%', #{fullName}), '%')


	and c.goods_name like CONCAT(CONCAT('%', #{goodsName}), '%')

order by a.addtime desc

 select *
	from order_main

  m_id = #{mId,jdbcType=INTEGER} and
 	id in (select order_id from order_shop where m_id = #{mId,jdbcType=INTEGER}

     and status = #{status,jdbcType=INTEGER}

 	)

     and order_no like CONCAT(CONCAT('%', #{orderNo,jdbcType=VARCHAR}), '%')


    and is_del = #{isDel,jdbcType=BIT}


     and addtime >= #{addtime,jdbcType=TIMESTAMP}


       and addtime <= #{paytime,jdbcType=TIMESTAMP}


 order by addtime desc

 select *
	from order_main

  id = #{id,jdbcType=INTEGER}

    and status= #{status,jdbcType=INTEGER}

 select *
	from order_main where 1=1 

    and cart_id = #{cartId,jdbcType=VARCHAR}

 select *
	from order_main where 1=1 and id = #{id,jdbcType=INTEGER}

    and m_id = #{mId,jdbcType=INTEGER}

select main.* from order_main main ,order_shop shop,order_sku sku where 1=1 and main.id = shop.order_id and main.id=sku.order_id and shop.id=sku.order_shop_id and main.`status`=2 and main.m_id=shop.m_id
and main.m_id = #{mId,jdbcType=INTEGER}
and date_format(main.addtime, '%Y%m') = date_format(curdate() , '%Y%m')

select SUM(main.order_price) from order_main main ,order_shop shop,order_sku sku where 1=1 and main.id = shop.order_id and main.id=sku.order_id and shop.id=sku.order_shop_id and main.`status`=2 and main.m_id=shop.m_id
and main.m_id = #{mId,jdbcType=INTEGER}
and date_format(main.addtime, '%Y%m') = date_format(curdate() , '%Y%m')

select main.* from member_user_address a,order_main main where 1=1 and main.m_addr_id=a.id and main.`status`=2
and main.m_id = #{mId,jdbcType=INTEGER}
and main.m_addr_id = #{mAddrId,jdbcType=INTEGER}

select main.* from order_main main
where 1=1  

   and main.m_id = #{mId,jdbcType=INTEGER}


   and main.cart_id = #{cartId,jdbcType=VARCHAR}

  order by main.addtime desc

select main.id,main.m_id,main.order_no,main.addtime,main.`status`,oshop.shop_id,main.fz, cast(main.order_price/100 as decimal(20,2)) as real_price, osku.goods_name as goods_name, osku.sku_image as goods_image, osku.sku_num as goods_num, cast(osku.sku_sell_price_real/100 as decimal(20,2)) as price, osku.sku_value as value, mshop.shop_name as shop_name, mshop.linkman_phone as shop_phone, maddress.provname as provname, maddress.cityname as cityname, maddress.areaname as areaname, maddress.fourname as fourname, maddress.address as address, maddress.full_name as full_name, maddress.tel as tel, m.username as user_nick, m.phone as phone, gsku.jd_sku_no as jd_sku_no, cast(gsku.jd_price/100 as decimal(20,2)) as jd_pirce, cast(gsku.jd_protocol_price/100 as decimal(20,2)) as jd_protocol_price, oshop.jdOrderId as jdOrderId, oshop.remark as remark,oshop.is_refund as is_refund,oshop.id as order_shop_id,oshop.status as order_shop_status, osku.id as order_sku_id, osku.is_refund as isRefund from order_main main,order_shop oshop,order_sku osku, member_shop mshop,member m,member_user_address maddress, goods_sku gsku
where main.id=osku.order_id and main.id=oshop.order_id and oshop.id=osku.order_shop_id and oshop.shop_id=mshop.m_id and main.m_id=m.id and maddress.id=main.m_addr_id and osku.sku_id=gsku.id
and main.status = #{status}
and main.order_no like CONCAT('%',#{orderNo}, '%')
and oshop.shop_order_no like CONCAT('%',#{shopOrderNo}, '%')
and main.addtime >= #{startTime}
and main.addtime <= #{endTime}
and m.id = #{id}
and oshop.shop_id = #{shopId}
and m.phone like CONCAT('%',#{tel}, '%')
and m.username like CONCAT('%',#{fullName}, '%')
and osku.goods_name like CONCAT('%', #{goodsName}, '%')
order by main.addtime desc

select count(main.id) from order_main main,order_shop oshop,order_sku osku, member_shop mshop,member m,member_user_address maddress, goods_sku gsku
where main.id=osku.order_id and main.id=oshop.order_id and oshop.id=osku.order_shop_id and main.shop_id=mshop.m_id and main.m_id=m.id and maddress.id=main.m_addr_id and osku.sku_id=gsku.id
and main.status = #{status}
