    from goods a left join goods_sku b on a.id=b.goods_id left join goods_category c on a.cat_id=c.id
    where 1=1
    and a.name like CONCAT(CONCAT('%', #{0}), '%')

    	and a.topic_type = #{topicType,jdbcType=INTEGER}


