mapper.select:
select id, name, sell_price, market_price, image, team_price, fixed_sale, topic_type, topic_goods_id from goods where status = 5 and visible = 0 and sale_type = 1 and tag_name not like "%3%"
and name like CONCAT(CONCAT('%', #{name}), '%') and (topic_type = 0 or topic_type = 6)
and topic_type = 0
and is_new_shop = #{isNewShop,jdbcType=INTEGER}
and is_hot = #{isHot,jdbcType=BIT}
and is_new = #{isNew,jdbcType=BIT}
order by sortnum asc

select g.id, g.name, gsku.sell_price as sell_price, g.image,g.sale, g.fixed_sale, g.topic_type,g.sale_type from goods g,goods_sku gsku where g.id=gsku.goods_id and gsku.status=0 and g.status = 5 and g.visible = 0 and g.sale_type = 1 and g.tag_name not like '%3%'
and g.name like CONCAT(CONCAT('%', #{name}), '%') and (g.topic_type = 0 or g.topic_type = 6)
and g.topic_type = 0
and g.is_new_shop = #{isNewShop,jdbcType=INTEGER}
and g.is_hot = #{isHot,jdbcType=BIT}
and g.is_new = #{isNew,jdbcType=BIT}
group by g.id order by g.sortnum asc limit #{currentPageIndex,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

