and a.cat_id = #{catId,jdbcType=BIGINT}
and a.sale_type = #{saleType,jdbcType=INTEGER}
and a.topic_type = #{topicType,jdbcType=INTEGER}
and a.id = #{id,jdbcType=INTEGER}
and b.sku_no like CONCAT(CONCAT('%', #{skuNo}), '%')
and b.jd_sku_no = #{jdSkuNo}
and a.status = #{status,jdbcType=INTEGER}
and a.status !=5
and ( (b.sell_price >= #{startPrice} and b.sell_price <= #{endPrice}) or (b.team_price >= #{startPrice} and b.team_price <= #{endPrice}) )
and (b.sell_price >= #{startPrice} or b.team_price >= #{startPrice})
and (b.sell_price <= #{endPrice} or b.team_price <= #{endPrice})
GROUP BY a.id
order by a.addtime desc
