mapper.select:
select val.* from goods_price_approval val,goods g,goods_sku sku where 1=1 and g.id=sku.goods_id and sku.id=val.goods_sku_id and val.goods_id = g.id and val.is_del=0
and g.name like CONCAT(CONCAT('%', #{goodsName,jdbcType=VARCHAR}), '%')
and sku.sku_no in
#{gskuList}
and val.goods_id = #{goodsId,jdbcType=INTEGER}
and val.status = #{status,jdbcType=INTEGER}
and reply_no = #{replyNo,jdbcType=VARCHAR}
order by val.reply_time desc

select val.* from goods_price_approval val,goods g,goods_sku sku where 1=1 and g.id=sku.goods_id and sku.id=val.goods_sku_id and val.goods_id = g.id and val.is_del=0
and g.name like CONCAT(CONCAT('%', #{goodsName,jdbcType=VARCHAR}), '%')
and sku.sku_no in
#{gskuList}
and val.goods_id = #{goodsId,jdbcType=INTEGER}
and val.status = #{status,jdbcType=INTEGER}
and reply_no = #{replyNo,jdbcType=VARCHAR}
group by val.goods_sku_id order by val.reply_time desc
