mapper.select:
select a.* from topic_goods a left join goods b on a.goods_id = b.id left join topic c on a.act_id = c.id left join goods_sku d on b.id=d.goods_id where a.act_id = #{actId} and a.is_delete = 0
and a.sid=#{shopId}
and a.id = #{id}
and b.name like CONCAT(CONCAT('%', #{goodsName}), '%')
and a.c_time >= #{cStime}
and a.c_time <= #{cEtime}
group by d.goods_id
order by a.listorder asc
order by a.listorder desc
order by d.team_price asc
order by d.team_price desc
order by a.c_time desc

select a.*
from topic_goods a left join goods b on a.goods_id = b.id left join topic c on a.act_id = c.id left join topic_bargain_config d on a.id = d. tg_id
where a.act_id = #{actId} and a.id in (select tg_id from topic_bargain_config)

	and a.id = #{id}


    and b.name like CONCAT(CONCAT('%', #{goodsName}), '%')


	 and  c.start_time <= #{actTime}
     and  c.end_time >= #{actTime}


	 and  c.apply_stime <= #{applyTime}
     and  c.apply_stime >= #{applyTime}



		order by a.listorder asc


		order by a.listorder desc


		order by d.act_price asc


		order by d.act_price desc


		order by a.c_time desc

select count(*)
from topic_goods
where act_id = #{actId,jdbcType=INTEGER}

select tg.* from topic_goods tg,topic tc,goods_sku sku,goods g where 1=1 and g.id=tg.goods_id and g.id=sku.goods_id and tg.act_id=tc.id and tg.is_delete=0 and tc.type=4 and tc.is_delete=0 and g.status=5 and g.visible=0 and tg.`status` = 1
and tg.goods_id = #{goodsId,jdbcType=INTEGER}
and tc.end_time > NOW() group by tg.id

select tg.* from topic_goods tg,topic tc,goods_sku sku,goods g where 1=1 and g.id=tg.goods_id and g.id=sku.goods_id and tg.act_id=tc.id and tg.is_delete=0 and tc.type=7 and g.status=5 and g.visible=0 and tc.is_delete=0 and tg.`status` = 1
and tg.goods_id = #{goodsId,jdbcType=INTEGER}
and tc.end_time > NOW() group by tg.id
